import React, { Component } from 'react';
import axios from 'axios';
import '../style/PlantPage.css'; // Import CSS file

class PlantPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            plants: [],
            selectedRow: null
        };
    }

    componentDidMount() {
        // Fetch plant data from Java Spring backend
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }

    handleRowClick = (plantId) => {
        this.setState({ selectedRow: plantId });
    };

    handleDelete = (id) => {
        axios.delete(`http://localhost:8080/plant/deleteById?id=${id}`)
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });

        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });

        this.forceUpdate();
    };

    handleEdit = (plantId) => {
        const { role, onChangePage } = this.props;
        onChangePage(role, 4, plantId, 0);
    };

    render() {
        return (
            <div className="PlantPage">
                <h1>Plants</h1>
                <table>
                    <thead>
                    <tr>
                        <th>No.</th>
                        <th>Plant name</th>
                        <th>Species</th>
                        <th>Plant type</th>
                        <th>Carnivorous</th>
                        <th>Endangered</th>
                        <th>Location</th>
                        <th>Image</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.plants.map(plant => (
                        <tr key={plant.id}
                            onClick={() => this.handleRowClick(plant.id)}
                            className={this.state.selectedRow === plant.id ? 'selected' : ''}>
                            <td>{plant.id}</td>
                            <td>{plant.plantName}</td>
                            <td>{plant.species}</td>
                            <td>{plant.plantType}</td>
                            <td>{plant.carnivorous}</td>
                            <td>{plant.endangered}</td>
                            <td>{plant.location}</td>
                            <td><img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px' }} /></td>
                            <td>
                                <button onClick={() => this.handleDelete(plant.id)}>Delete</button>
                                <button onClick={() => this.handleEdit(plant.id)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default PlantPage;
