import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../style/PlantPage.css'; // Import CSS file

const PlantPage = () => {
    const [plants, setPlants] = useState([]);
    const [selectedRows, setSelectedRows] = useState([]);

    useEffect(() => {
        // Fetch plant data from Java Spring backend
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                setPlants(response.data);
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }, []);

    const handleRowClick = (plantId) => {
        const isSelected = selectedRows.includes(plantId);
        if (isSelected) {
            setSelectedRows(selectedRows.filter(id => id !== plantId));
        } else {
            setSelectedRows([...selectedRows, plantId]);
        }
    };

    return (
        <div className="PlantPage">
            <h1>Plants</h1>
            <table>
                <thead>
                <tr>
                    <th>Plant name</th>
                    <th>Species</th>
                    <th>Plant type</th>
                    <th>Carnivorous</th>
                    <th>Endangered</th>
                    <th>Location</th>
                </tr>
                </thead>
                <tbody>
                {plants.map(plant => (
                    <tr key={plant.id}
                        onClick={() => handleRowClick(plant.id)}
                        className={selectedRows.includes(plant.id) ? 'selected' : ''}>
                        <td>{plant.plantName}</td>
                        <td>{plant.species}</td>
                        <td>{plant.plantType}</td>
                        <td>{plant.carnivorous}</td>
                        <td>{plant.endangered}</td>
                        <td>{plant.location}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default PlantPage;
