import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../style/UpdateNewUser.css'

const UpdateNewUser = ({ userId }) => {
    const [user, setUser] = useState({
        username: '',
        password: '',
        adminStatus: ''
    });
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        dateOfBirth: '',
        address: '',
        phoneNumber: '',
        email: ''
    });

    useEffect(() => {
        if (userId) {
            // Fetch user data for editing if plantId is provided
            axios.get(`http://localhost:8080/users/findById?id=${userId}`)
                .then(response => {
                    setUser(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user data:', error);
                });

            axios.get(`http://localhost:8080/employee/findById?id=${user.person}`)
                .then(response => {
                    setEmployee(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user data:', error);
                });
        }
    }, [userId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPlant(prevPlant => ({
            ...prevPlant,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (plantId) {
            // Update existing plant data
            axios.put(`http://localhost:8080/users/update/`, user)
                .then(response => {
                    console.log('User updated successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error updating user data:', error);
                });
        } else {
            // Add new plant data
            axios.post('http://localhost:8080/users/add', user)
                .then(response => {
                    console.log('New user added successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error adding new user:', error);
                });
        }
    };


    return (
        <div>
            <h2>{plantId ? 'Edit Plant' : 'Add New Plant'}</h2>
            <form onSubmit={handleSubmit}>
                <label>Plant name:</label>
                <input type="text" name="name" value={plant.plantName} onChange={handleChange} />
                <label>Species:</label>
                <input type="text" name="species" value={plant.species} onChange={handleChange} />
                <label>Plant type:</label>
                <input type="text" name="type" value={plant.plantType} onChange={handleChange} />
                <label>Carnivorous:</label>
                <input type="text" name="carnivorous" value={plant.carnivorous} onChange={handleChange} />
                <label>Endangered:</label>
                <input type="text" name="endangered" value={plant.endangered} onChange={handleChange} />
                <label>Location:</label>
                <input type="text" name="location" value={plant.location} onChange={handleChange} />
                <label>Image Data:</label>
                <div align={"center"}>
                    <img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px', alignContent: "center"}} />
                </div>
                <button type="submit">{plantId ? 'Update Plant' : 'Add Plant'}</button>
            </form>
        </div>
    );
};

export default UpdateNewUser;
