import React, { Component } from 'react';
import axios from 'axios';
import '../style/UpdateNewPlant.css';

class UpdateNewPlant extends Component {
    constructor(props) {
        super(props);
        this.state = {
            plant: {
                plantName: '',
                species: '',
                plantType: '',
                carnivorous: '',
                endangered: '',
                location: '',
                img: '', // Assuming imageData is a base64-encoded string for simplicity
            },
            lastLang: null,
            dict: []
        };
    }

    componentDidMount() {
        const { plantId } = this.props;
        if (plantId) {
            // Fetch plant data for editing if plantId is provided
            axios.get(`http://localhost:8080/plant/findById?id=${plantId}`)
                .then(response => {
                    this.setState({ plant: response.data });
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }

    onButtonClickCancel = () => {
        const { role, onChangePage, lang } = this.props;
        onChangePage(role, 1, 0, 0, lang);
    };

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState(prevState => ({
            plant: {
                ...prevState.plant,
                [name]: value
            }
        }));
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const { role, onChangePage, lang, plant } = this.props;
        const { plantId } = this.props;

        if (plantId) {
            // Update existing plant data
            axios.put(`http://localhost:8080/plant/update`, plant)
                .then(response => {
                    console.log('Plant updated successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error updating plant data:', error);
                });
        } else {
            // Add new plant data
            axios.post('http://localhost:8080/plant/add', plant)
                .then(response => {
                    console.log('New plant added successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error adding new plant:', error);
                });
        }

        onChangePage(role, 1, 0, 0, lang);
    };

    async getBase64(file) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader()
            reader.readAsDataURL(file)
            reader.onload = () => {
                resolve(reader.result)
            }
            reader.onerror = reject
        })
    }

    updatePhoto = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                let formData = new FormData();

                //update or add image
                await this.getBase64(file) // `file` your img file
                    .then(res => {
                        this.setState(prevState => ({
                            plant: {
                                ...prevState.plant,
                                img: res.split(',')[1]
                            }
                        }));
                        formData.append('id', this.props.plantId); // Assuming plantId is accessible here
                        formData.append('file', res.split(',')[1]);

                        axios.post(`http://localhost:8080/plant/photo`, formData)
                            .then(response => {
                                console.log('New photo added successfully:', response.data);
                                // Optionally redirect to another page or display a success message
                            })
                            .catch(error => {
                                console.error('Error adding photo:', error);
                            });
                        console.log(res);
                    })
                    .catch(err => console.log(err))
            } catch (error) {
                console.log(error);
            }
        }
    };

    render() {
        const { plant } = this.state;
        const { plantId } = this.props;
        if (this.props.lang !== this.state.lastLang) {
            axios.get(`http://localhost:8080/lang/get?lang=${this.props.lang}`)
                .then(response => {
                    this.setState({dict: response.data, lastLang: this.props.lang });
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching language info:', error);
                });
        }

        return (
            <div>
                <h2>{plantId ? (this.state.dict["lang"] ? this.state.dict["lang"]["Edit"] : "Edit plant") : (this.state.dict["lang"] ? this.state.dict["lang"]["Add plant"] : "Add plant")}</h2>
                <form onSubmit={this.handleSubmit}>
                    <label>{this.state.dict["lang"] ? this.state.dict["lang"][""] : "Edit plant"}</label>
                    <input type="text" name="name" value={plant.plantName} onChange={this.handleChange} />
                    <label>Species:</label>
                    <input type="text" name="species" value={plant.species} onChange={this.handleChange} />
                    <label>Plant type:</label>
                    <input type="text" name="type" value={plant.plantType} onChange={this.handleChange} />
                    <label>Carnivorous:</label>
                    <input type="text" name="carnivorous" value={plant.carnivorous} onChange={this.handleChange} />
                    <label>Endangered:</label>
                    <input type="text" name="endangered" value={plant.endangered} onChange={this.handleChange} />
                    <label>Location:</label>
                    <input type="text" name="location" value={plant.location} onChange={this.handleChange} />
                    <label>Image Data:</label>
                    <div align={"center"}>
                        <img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px', alignContent: "center"}} />
                    </div>
                    <div align={"center"}>
                        <input type='file' onChange={this.updatePhoto} name='file' accept='image/*' />
                    </div>
                    <div>
                        <button onClick={this.onButtonClickCancel}>Cancel</button>
                        <button type="submit">{plantId ? 'Update Plant' : 'Add Plant'}</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default UpdateNewPlant;
