import React, { Component } from 'react';
import axios from 'axios';
import '../style/UserPage.css'; // Import CSS file

class UserPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            employees: [],
            selectedRow: null,
            lastLang: null,
            dict: []
        };

        this.presenter = new UserPresenter(this);
    }

    componentDidMount() {
        this.fetchData();
    }

    componentWillUnmount() {
        // Cancel any pending axios requests when the component unmounts
        this.cancelRequests();
    }

    cancelRequests() {
        // Implement cancellation logic for axios requests if needed
    }

    fetchData() {
        axios.all([
            axios.get('http://localhost:8080/users/list'),
            axios.get('http://localhost:8080/employee/list')
        ])
            .then(axios.spread((usersResponse, employeesResponse) => {
                this.setState({
                    users: usersResponse.data,
                    employees: employeesResponse.data
                });
            }))
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    handleRowClick = (userId) => {
        this.setState({ selectedRow: userId });
    };

    handleDelete = (id) => {
        axios.delete(`http://localhost:8080/users/deleteById?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    users: prevState.users.filter(user => user.userID !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });

        axios.delete(`http://localhost:8080/employee/deleteByUser?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    employees: prevState.employees.filter(emp => emp.id !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    };

    handleEdit = (userId) => {
        const { role, onChangePage, lang } = this.props;
        onChangePage(role, 5, 0, userId, lang);
    };

    showAll = () => {
        this.fetchData();
    };

    showAdmins = () => {
        axios.get('http://localhost:8080/users/filter?status=yes')
            .then(response => {
                this.setState({
                    users: response.data
                });
                axios.get('http://localhost:8080/employee/filter?status=yes')
                    .then(resp => {
                        this.setState( {
                            employees :resp.data
                        });
                    })
                    .catch(error => {
                        console.error('Error fetching employee data:', error);
                    });
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    };

    showEmp = () => {
        axios.get('http://localhost:8080/users/filter?status=no')
            .then(response => {
                this.setState({
                    users: response.data
                });
                axios.get('http://localhost:8080/employee/filter?status=no')
                    .then(resp => {
                        this.setState( {
                            employees :resp.data
                        });
                    })
                    .catch(error => {
                        console.error('Error fetching employee data:', error);
                    });
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    };

    render() {
        const { users, employees, selectedRow } = this.state;
        if (this.props.lang !== this.state.lastLang) {
            axios.get(`http://localhost:8080/lang/get?lang=${this.props.lang}`)
                .then(response => {
                    this.setState({dict: response.data, lastLang: this.props.lang });
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching language info:', error);
                });
        }

        return (
            <div className="UserPage">
                <h1>{this.state.dict["lang"] ? this.state.dict["lang"]["User page"] : "Users"}</h1>
                <div>
                    <button className="filterB" onClick={this.showAll}>{this.state.dict["lang"] ? this.state.dict["lang"]["Show"] : "Show all"}</button>
                    <button className="filterB" onClick={this.showAdmins}>{this.state.dict["lang"] ? this.state.dict["lang"]["Show admin"] : "Show only administrators"}</button>
                    <button className="filterB" onClick={this.showEmp}>{this.state.dict["lang"] ? this.state.dict["lang"]["Show emp"] : "Show only employees"}</button>
                </div>
                <table>
                    <thead>
                    <tr>
                        <th>No.</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["First name"] : "First name"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Last name"] : "Last name"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["DOB"] : "Date of birth"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Address"] : "Address"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Phone"] : "Phone number"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Email"] : "Email"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Username"] : "Username"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Password"] : "Password"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Status"] : "Admin status"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Actions"] : "Actions"}</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map( (user, i) => (
                        <tr key={user.userID}
                            onClick={() => this.handleRowClick(user.userID)}
                            className={selectedRow === user.userID ? 'selected' : ''}>
                            <td>{user.userID}</td>
                            <td>{employees[i]?.firstName}</td>
                            <td>{employees[i]?.lastName}</td>
                            <td>{employees[i]?.dateOfBirth}</td>
                            <td>{employees[i]?.address}</td>
                            <td>{employees[i]?.phoneNumber}</td>
                            <td>{employees[i]?.email}</td>
                            <td>{user.username}</td>
                            <td>{user.password}</td>
                            <td>{user.adminStatus}</td>
                            <td>
                                <button onClick={() => this.handleDelete(user.userID)}>{this.state.dict["lang"] ? this.state.dict["lang"]["Delete"] : "Delete"}</button>
                                <button onClick={() => this.handleEdit(user.userID)}>{this.state.dict["lang"] ? this.state.dict["lang"]["Edit"] : "Edit"}</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default UserPage;
