import React, { useEffect, useRef, useState } from 'react';
import Chart from 'chart.js/auto';

const StatisticsPage = () => {
    const [chartData, setChartData] = useState(null);
    const pieChartRef = useRef(null);
    const barChart1Ref = useRef(null);
    const barChart2Ref = useRef(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const pieResponse = await fetch('http://localhost:8080/plant/pie');
                const bar1Response = await fetch('http://localhost:8080/plant/bar1');
                const bar2Response = await fetch('http://localhost:8080/plant/bar2');

                if (!pieResponse.ok || !bar1Response.ok || !bar2Response.ok) {
                    throw new Error('Failed to fetch data');
                }

                const pieData = await pieResponse.json();
                const bar1Data = await bar1Response.json();
                const bar2Data = await bar2Response.json();

                const pieChartData = {
                    labels: pieData.filter((_, index) => index % 2 !== 0),
                    datasets: [{
                        data: pieData.filter((_, index) => index % 2 === 0),
                        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
                        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56']
                    }]
                };

                const barChartData1 = {
                    labels: ['No', 'Yes'],
                    datasets: [{
                        label: 'Dataset 1',
                        data: bar1Data,
                        backgroundColor: 'rgba(255, 99, 132, 0.2)',
                        borderColor: 'rgba(255, 99, 132, 1)',
                        borderWidth: 1
                    }]
                };

                const barChartData2 = {
                    labels: bar2Data.filter((_, index) => index % 2 !== 0),
                    datasets: [{
                        label: 'Dataset 2',
                        data: bar2Data.filter((_, index) => index % 2 === 0),
                        backgroundColor: 'rgba(54, 162, 235, 0.2)',
                        borderColor: 'rgba(54, 162, 235, 1)',
                        borderWidth: 1
                    }]
                };

                setChartData({ pieChartData, barChartData1, barChartData2 });
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        if (chartData) {
            destroyChart(pieChartRef.current);
            destroyChart(barChart1Ref.current);
            destroyChart(barChart2Ref.current);

            renderChart(pieChartRef.current, chartData.pieChartData, 'pie');
            renderChart(barChart1Ref.current, chartData.barChartData1, 'bar');
            renderChart(barChart2Ref.current, chartData.barChartData2, 'bar');
        }
    }, [chartData]);

    const destroyChart = (chartRef) => {
        if (chartRef) {
            chartRef.destroy();
        }
    };

    const renderChart = (chartRef, data, type) => {
        if (chartRef) {
            new Chart(chartRef, {
                type: type,
                data: data,
                options: {
                    responsive: true,
                    maintainAspectRatio: false
                }
            });
        }
    };

    return (
        <div>
            <h1>Statistics Page</h1>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <div style={{ width: '50%' }}>
                    <canvas id="pieChart" ref={pieChartRef}></canvas>
                </div>
                <div style={{ width: '50%' }}>
                    <canvas id="barChart1" ref={barChart1Ref}></canvas>
                </div>
                <div style={{ width: '50%' }}>
                    <canvas id="barChart2" ref={barChart2Ref}></canvas>
                </div>
            </div>
        </div>
    );
};

export default StatisticsPage;
