import React, { Component } from 'react';
import axios from 'axios';
import '../style/PlantPage.css'; // Import CSS file
import Select from 'react-dropdown-select';

class PlantPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            plants: [],
            selectedRow: null,
            textToSearch: ''
        };

        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        // Fetch plant data from Java Spring backend
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }

    handleRowClick = (plantId) => {
        this.setState({ selectedRow: plantId });
    };

    handleDelete = (id) => {
        axios.delete(`http://localhost:8080/plant/deleteById?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    plants: prevState.plants.filter(plant => plant.id !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    };

    handleChange = (e) => {
        const { value } = e.target;
        this.setState({ textToSearch: value });
    };

    handleEdit = (plantId) => {
        const { role, onChangePage } = this.props;
        onChangePage(role, 4, plantId, 0);
    };

    handleSearch() {
        axios.get(`http://localhost:8080/plant/findByName?name=${this.state.textToSearch}`)
            .then(response => {
                console.log(response.data);
                axios.get('http://localhost:8080/plant/list')
                    .then(responseFull => {
                        this.setState({ plants: responseFull.data.filter(plant => plant.id === response.data.id) });
                    })
                    .catch(error => {
                        console.error('Error fetching plant data:', error);
                    });
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    }

    showAll() {
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }

    onChangeSort(values) {
        console.log(values);
        if (values === null || values.length === 0) {
            this.showAll();
        }
        else {
            axios.get(`http://localhost:8080/plant/sort?sortBy=${values[0].value}`)
                .then(response => {
                    this.setState({plants: response.data});
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }

    onChangeFilter(values) {
        console.log(values);
        if (values === null || values.length === 0) {
            this.showAll();
        }
        else {
            axios.get(`http://localhost:8080/plant/filter?criterion=${values[0].value}&value=${this.state.textToSearch}`)
                .then(response => {
                    this.setState({plants: response.data});
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }

    onChangeSave(values) {

    }

    render() {
        const sortOptions = [
            {
                value: 'Tip',
                label: 'Tip'
            },
            {
                value: 'Specie',
                label: 'Specie'
            }
        ];

        const filterOptions = [
            {
                value: 1,
                label: 'Tip'
            },
            {
                value: 2,
                label: 'Specie'
            },
            {
                value: 3,
                label: 'Carnivore'
            },
            {
                value: 4,
                label: 'Pe cale de disparitie'
            },
            {
                value: 5,
                label: 'Locatie'
            }
        ];

        const saveOptions = [
            {
                value: 1,
                label: 'csv'
            },
            {
                value: 2,
                label: 'json'
            },
            {
                value: 3,
                label: 'xml'
            },
            {
                value: 4,
                label: 'doc'
            }
        ];

        return (
            <div className="PlantPage">
                <h1>Plants</h1>
                <div>
                    <div className="combobox" style={{ width: 300, display: "flex" }}>
                        <label style={{margin: 9}}>Sort by: </label>
                        <Select
                            multi
                            options={sortOptions}
                            onChange={(values) => this.onChangeSort(values)}
                        />
                    </div>
                    <div className="combobox" style={{ width: 300, display: "flex"}}>
                        <label style={{margin: 6}}>Filter by: </label>
                        <Select
                            multi
                            options={filterOptions}
                            onChange={(values) => this.onChangeFilter(values)}
                        />
                    </div>
                    <div className="combobox" style={{ width: 300, display: "flex"}}>
                        <label style={{margin: 6}}>Save as: </label>
                        <Select
                            multi
                            options={saveOptions}
                            onChange={(values) => this.onChangeSave(values)}
                        />
                    </div>
                </div>
                <div>
                    <input type="text" value={this.state.textToSearch} style={{ width: 500 }} onChange={this.handleChange}/>
                    <button className="statistics" onClick={() => this.handleSearch()}>Search</button>
                    <button className="statistics" onClick={() => this.showAll()}>Show all</button>
                </div>
                <table>
                    <thead>
                    <tr>
                        <th>No.</th>
                        <th>Plant name</th>
                        <th>Species</th>
                        <th>Plant type</th>
                        <th>Carnivorous</th>
                        <th>Endangered</th>
                        <th>Location</th>
                        <th>Image</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.plants.map(plant => (
                        <tr key={plant.id}
                            onClick={() => this.handleRowClick(plant.id)}
                            className={this.state.selectedRow === plant.id ? 'selected' : ''}>
                            <td>{plant.id}</td>
                            <td>{plant.plantName}</td>
                            <td>{plant.species}</td>
                            <td>{plant.plantType}</td>
                            <td>{plant.carnivorous}</td>
                            <td>{plant.endangered}</td>
                            <td>{plant.location}</td>
                            <td><img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px' }} /></td>
                            <td>
                                <button onClick={() => this.handleDelete(plant.id)}>Delete</button>
                                <button onClick={() => this.handleEdit(plant.id)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>

                <button className="statistics">View statistics</button>
            </div>
        );
    }
}

export default PlantPage;
