import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../style/LogInPage.css'
import axios from "axios";

const Login = ({role, onChangePage, lang}) => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [usernameError, setUsernameError] = useState('')
    const [passwordError, setPasswordError] = useState('')
    const [user, setUser] = useState({
        username: '',
        person: '',
        password: '',
        adminStatus: '',
        lastLang: null
    });
    const [error, setError] = useState(true);

    const onButtonClickLogIn = () => {
        axios.get(`http://localhost:8080/users/login?username=${username}&password=${password}`)
            .then(response => {
                setUser(response.data);
                if (response.data) {
                    if (response.data.adminStatus === 'yes') {
                        onChangePage("administrator", 1, 0, 0, lang);
                    } else {
                        onChangePage("employee", 1, 0, 0, lang);
                    }
                }
                else {
                    setError(false);
                }
            })
            .catch(error => {
                console.error('Error during login:', error);
            });
    }

    const onButtonClickCancel = () => {
        onChangePage(role, 1, 0, 0, lang);
    }


    return (
        <div className={'mainContainer'}>
            <div className={'titleContainer'}>
                <div>Login</div>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input
                    value={username}
                    placeholder="Enter username"
                    onChange={(ev) => setUsername(ev.target.value)}
                    className={'inputBox'}
                />
                <label className="errorLabel">{usernameError}</label>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input
                    type="password"
                    value={password}
                    placeholder="Enter password"
                    onChange={(ev) => setPassword(ev.target.value)}
                    className={'inputBox'}
                />
                <label className="errorLabel">{passwordError}</label>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input className={'inputButton'} type="button" onClick={onButtonClickCancel} value={'Cancel'} />
                <input className={'inputButton'} type="button" onClick={onButtonClickLogIn} value={'Log in'} />
            </div>

            <par hidden={!!error} style={{ color:'red', size: 24}}>Error during login!</par>
        </div>
    )
}

export default Login
