import React, { useEffect, useState } from 'react';
import Chart from 'chart.js/auto';

const StatisticsPage = () => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const pieResponse = await fetch('http://localhost:8080/plant/pie');
                const bar1Response = await fetch('http://localhost:8080/plant/bar1');
                const bar2Response = await fetch('http://localhost:8080/plant/bar2');

                if (!pieResponse.ok || !bar1Response.ok || !bar2Response.ok) {
                    throw new Error('Failed to fetch data');
                }

                const pieData = await pieResponse.json();
                const bar1Data = await bar1Response.json();
                const bar2Data = await bar2Response.json();

                // Process data for pie chart
                const pieChartData = {
                    labels: pieData.filter((_, index) => index % 2 !== 0),
                    datasets: [{
                        data: pieData.filter((_, index) => index % 2 === 0),
                        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
                        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56']
                    }]
                };

                // Process data for bar chart 1
                const barChartData1 = {
                    labels: ['No', 'Yes'],
                    datasets: [{
                        label: 'Dataset 1',
                        data: bar1Data,
                        backgroundColor: 'rgba(255, 99, 132, 0.2)',
                        borderColor: 'rgba(255, 99, 132, 1)',
                        borderWidth: 1
                    }]
                };

                // Process data for bar chart 2
                const barChartData2 = {
                    labels: bar2Data.filter((_, index) => index % 2 !== 0),
                    datasets: [{
                        label: 'Dataset 2',
                        data: bar2Data.filter((_, index) => index % 2 === 0),
                        backgroundColor: 'rgba(54, 162, 235, 0.2)',
                        borderColor: 'rgba(54, 162, 235, 1)',
                        borderWidth: 1
                    }]
                };

                setChartData({ pieChartData, barChartData1, barChartData2 });
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
    }, []);

    useEffect(() => {
        if (chartData) {
            const renderChart = (ctx, data, type) => {
                new Chart(ctx, {
                    type: type,
                    data: data,
                    options: {
                        responsive: true,
                        maintainAspectRatio: false
                    }
                });
            };

            renderChart(document.getElementById('pieChart').getContext('2d'), chartData.pieChartData, 'pie');
            renderChart(document.getElementById('barChart1').getContext('2d'), chartData.barChartData1, 'bar');
            renderChart(document.getElementById('barChart2').getContext('2d'), chartData.barChartData2, 'bar');
        }
    }, [chartData]);

    return (
        <div>
            <h1>Statistics Page</h1>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <div style={{ width: '50%' }}>
                    <canvas id="pieChart"></canvas>
                </div>
                <div style={{ width: '50%' }}>
                    <canvas id="barChart1"></canvas>
                </div>
                <div style={{ width: '50%' }}>
                    <canvas id="barChart2"></canvas>
                </div>
            </div>
        </div>
    );
};

export default StatisticsPage;
