import React, { Component } from 'react';
import axios from 'axios';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            usernameError: '',
            passwordError: '',
            user: {
                username: '',
                person: '',
                password: '',
                adminStatus: '',
                lastLang: null
            },
            error: true,
            dict: {},
            lastLang: null
        };
    }

    onButtonClickLogIn = () => {
        axios.get(`http://localhost:8080/users/login?username=${this.state.username}&password=${this.state.password}`)
            .then(response => {
                this.setState({ user: response.data });
                if (response.data) {
                    if (response.data.adminStatus === 'yes') {
                        this.props.onChangePage("administrator", 1, 0, 0, this.props.lang);
                    } else {
                        this.props.onChangePage("employee", 1, 0, 0, this.props.lang);
                    }
                } else {
                    this.setState({ error: false });
                }
            })
            .catch(error => {
                console.error('Error during login:', error);
            });
    }

    onButtonClickCancel = () => {
        this.props.onChangePage(this.props.role, 1, 0, 0, this.props.lang);
    }

    componentDidMount() {
        this.fetchLanguageInfo();
    }

    fetchLanguageInfo() {
        if (this.props.lang !== this.state.lastLang) {
            axios.get(`http://localhost:8080/lang/get?lang=${this.props.lang}`)
                .then(response => {
                    this.setState({ dict: response.data, lastLang: this.props.lang });
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching language info:', error);
                });
        }
    }

    render() {
        const { username, password, error } = this.state;

        return (
            <div className={'mainContainer'}>
                <div className={'titleContainer'}>
                    <div>Login</div>
                </div>
                <br />
                <div className={'inputContainer'}>
                    <input
                        value={username}
                        placeholder="Enter username"
                        onChange={(ev) => this.setState({ username: ev.target.value })}
                        className={'inputBox'}
                    />
                    <label className="errorLabel">{this.state.usernameError}</label>
                </div>
                <br />
                <div className={'inputContainer'}>
                    <input
                        type="password"
                        value={password}
                        placeholder="Enter password"
                        onChange={(ev) => this.setState({ password: ev.target.value })}
                        className={'inputBox'}
                    />
                    <label className="errorLabel">{this.state.passwordError}</label>
                </div>
                <br />
                <div className={'inputContainer'}>
                    <input className={'inputButton'} type="button" onClick={this.onButtonClickCancel} value={'Cancel'} />
                    <input className={'inputButton'} type="button" onClick={this.onButtonClickLogIn} value={'Log in'} />
                </div>
                {error && <p style={{ color: 'red', fontSize: 24 }}>Error during login!</p>}
            </div>
        );
    }
}

export default Login;
