import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../style/LogInPage.css'
import axios from "axios";

const Login = ({onChangePage}) => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [usernameError, setUsernameError] = useState('')
    const [passwordError, setPasswordError] = useState('')


    const onButtonClickLogIn = () => {
        axios.get(`http://localhost:8080/users/login?id=${username}`)
            .then(response => {
                setPlant(response.data);
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });


        onChangePage("", 2);
    }

    const onButtonClickCancel = () => {
        // You'll update this function later...
    }


    return (
        <div className={'mainContainer'}>
            <div className={'titleContainer'}>
                <div>Login</div>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input
                    value={username}
                    placeholder="Enter username"
                    onChange={(ev) => setUsername(ev.target.value)}
                    className={'inputBox'}
                />
                <label className="errorLabel">{usernameError}</label>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input
                    value={password}
                    placeholder="Enter password"
                    onChange={(ev) => setPassword(ev.target.value)}
                    className={'inputBox'}
                />
                <label className="errorLabel">{passwordError}</label>
            </div>
            <br />
            <div className={'inputContainer'}>
                <input className={'inputButton'} type="button" onClick={onButtonClickCancel} value={'Cancel'} />
                <input className={'inputButton'} type="button" onClick={onButtonClickLogIn} value={'Log in'} />
            </div>
        </div>
    )
}

export default Login
