import React, { Component } from 'react';
import axios from 'axios';
import '../style/UserPage.css'; // Import CSS file

class UserPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            employees: [],
            selectedRow: null
        };
    }

    componentDidMount() {
        this.fetchData();
    }

    componentWillUnmount() {
        // Cancel any pending axios requests when the component unmounts
        this.cancelRequests();
    }

    cancelRequests() {
        // Implement cancellation logic for axios requests if needed
    }

    fetchData() {
        axios.all([
            axios.get('http://localhost:8080/users/list'),
            axios.get('http://localhost:8080/employee/list')
        ])
            .then(axios.spread((usersResponse, employeesResponse) => {
                this.setState({
                    users: usersResponse.data,
                    employees: employeesResponse.data
                });
            }))
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    handleRowClick = (userId) => {
        this.setState({ selectedRow: userId });
    };

    handleDelete = (id) => {
        axios.delete(`http://localhost:8080/users/deleteById?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    users: prevState.users.filter(user => user.userID !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });

        axios.delete(`http://localhost:8080/employee/deleteByUser?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    employees: prevState.employees.filter(emp => emp.id !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    };

    handleEdit = (userId) => {
        const { role, onChangePage } = this.props;
        onChangePage(role, 5, 0, userId);
    };

    showAll = () => {
        this.fetchData();
    };

    showAdmins = () => {
        axios.get('http://localhost:8080/users/filter?status=yes')
            .then(response => {
                this.setState({
                    users: response.data
                });
                axios.get('http://localhost:8080/employee/filter?status=yes')
                    .then(resp => {
                        this.setState( {
                            employees :resp.data
                        });
                    })
                    .catch(error => {
                        console.error('Error fetching employee data:', error);
                    });
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    };

    showEmp = () => {
        axios.get('http://localhost:8080/users/filter?status=no')
            .then(response => {
                this.setState({
                    users: response.data
                });
                axios.get('http://localhost:8080/employee/filter?status=no')
                    .then(resp => {
                        this.setState( {
                            employees :resp.data
                        });
                    })
                    .catch(error => {
                        console.error('Error fetching employee data:', error);
                    });
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    };

    render() {
        const { users, employees, selectedRow } = this.state;

        return (
            <div className="UserPage">
                <h1>Users</h1>
                <div>
                    <button className="filterB" onClick={this.showAll}>Show all</button>
                    <button className="filterB" onClick={this.showAdmins}>Show only administrators</button>
                    <button className="filterB" onClick={this.showEmp}>Show only employees</button>
                </div>
                <table>
                    <thead>
                    <tr>
                        <th>No.</th>
                        <th>First name</th>
                        <th>Last name</th>
                        <th>Date of birth</th>
                        <th>Address</th>
                        <th>Phone number</th>
                        <th>Email</th>
                        <th>Username</th>
                        <th>Password</th>
                        <th>Admin status</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map( (user, i) => (
                        <tr key={user.userID}
                            onClick={() => this.handleRowClick(user.userID)}
                            className={selectedRow === user.userID ? 'selected' : ''}>
                            <td>{user.userID}</td>
                            <td>{employees[i]?.firstName}</td>
                            <td>{employees[i]?.lastName}</td>
                            <td>{employees[i]?.dateOfBirth}</td>
                            <td>{employees[i]?.address}</td>
                            <td>{employees[i]?.phoneNumber}</td>
                            <td>{employees[i]?.email}</td>
                            <td>{user.username}</td>
                            <td>{user.password}</td>
                            <td>{user.adminStatus}</td>
                            <td>
                                <button onClick={() => this.handleDelete(user.userID)}>Delete</button>
                                <button onClick={() => this.handleEdit(user.userID)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default UserPage;
