import React, { Component } from 'react';
import Chart from 'chart.js/auto';
import axios from "axios";

class StatisticsPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chartData: null,
            lastLang: null,
            dict: []
        };
        this.pieChartRef = React.createRef();
        this.barChart1Ref = React.createRef();
        this.barChart2Ref = React.createRef();
    }

    componentDidMount() {
        this.fetchData();
    }

    fetchData = async () => {
        try {
            const pieResponse = await fetch('http://localhost:8080/plant/pie');
            const bar1Response = await fetch('http://localhost:8080/plant/bar1');
            const bar2Response = await fetch('http://localhost:8080/plant/bar2');

            if (!pieResponse.ok || !bar1Response.ok || !bar2Response.ok) {
                throw new Error('Failed to fetch data');
            }

            const pieData = await pieResponse.json();
            const bar1Data = await bar1Response.json();
            const bar2Data = await bar2Response.json();

            const pieChartData = {
                labels: pieData.filter((_, index) => index % 2 !== 0),
                datasets: [{
                    data: pieData.filter((_, index) => index % 2 === 0),
                    backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
                    hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56']
                }]
            };

            const barChartData1 = {
                labels: ['No', 'Yes'],
                datasets: [{
                    label: 'Plants by alimentation:',
                    data: bar1Data,
                    backgroundColor: 'rgba(255, 99, 132, 0.2)',
                    borderColor: 'rgba(255, 99, 132, 1)',
                    borderWidth: 1
                }]
            };

            const barChartData2 = {
                labels: bar2Data.filter((_, index) => index % 2 !== 0),
                datasets: [{
                    label: 'Plants by location:',
                    data: bar2Data.filter((_, index) => index % 2 === 0),
                    backgroundColor: 'rgba(54, 162, 235, 0.2)',
                    borderColor: 'rgba(54, 162, 235, 1)',
                    borderWidth: 1
                }]
            };

            this.setState({ chartData: { pieChartData, barChartData1, barChartData2 } });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    componentDidUpdate(prevProps, prevState) {
        if (prevState.chartData !== this.state.chartData && this.state.chartData) {
            this.destroyChart(this.pieChartRef);
            this.destroyChart(this.barChart1Ref);
            this.destroyChart(this.barChart2Ref);

            this.renderChart(this.pieChartRef, this.state.chartData.pieChartData, 'pie');
            this.renderChart(this.barChart1Ref, this.state.chartData.barChartData1, 'bar');
            this.renderChart(this.barChart2Ref, this.state.chartData.barChartData2, 'bar');
        }
    }

    componentWillUnmount() {
        this.destroyChart(this.pieChartRef);
        this.destroyChart(this.barChart1Ref);
        this.destroyChart(this.barChart2Ref);
    }

    destroyChart = (chartRef) => {
        if (chartRef.current && chartRef.current.chart) {
            chartRef.current.chart.destroy();
        }
    };

    renderChart = (chartRef, data, type) => {
        if (chartRef.current) {
            chartRef.current.chart = new Chart(chartRef.current, {
                type: type,
                data: data,
                options: {
                    responsive: true,
                    maintainAspectRatio: false
                }
            });
        }
    };

    render() {
        if (this.props.lang !== this.state.lastLang) {
            axios.get(`http://localhost:8080/lang/get?lang=${this.props.lang}`)
                .then(response => {
                    this.setState({dict: response.data, lastLang: this.props.lang });
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching language info:', error);
                });
        }

        return (
            <div>
                <h1>{this.state.dict["lang"] ? this.state.dict["lang"]["Statistics"] : "Statistics"}</h1>
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <h2>{this.state.dict["lang"] ? this.state.dict["lang"]["Stat1"] : "Plants by type"}:</h2>
                    <div style={{ width: '50%' }}>
                        <canvas id="pieChart" ref={this.pieChartRef}></canvas>
                    </div>
                    <div>
                        <h1></h1>
                    </div>
                    <h2>{this.state.dict["lang"] ? this.state.dict["lang"]["Stat2"] : "Plants by alimentation"}:</h2>
                    <div style={{ width: '50%' }}>
                        <canvas id="barChart1" ref={this.barChart1Ref}></canvas>
                    </div>
                    <div>
                        <h1></h1>
                    </div>
                    <h2>{this.state.dict["lang"] ? this.state.dict["lang"]["Stat3"] : "Plants by location"}:</h2>
                    <div style={{ width: '50%' }}>
                        <canvas id="barChart2" ref={this.barChart2Ref}></canvas>
                    </div>
                </div>
            </div>
        );
    }
}

export default StatisticsPage;
