import React, { useEffect, useState } from "react";
import Login from "./view/LogInPage";
import PlantPage from "./view/PlantPage";
import UserPage from "./view/UserPage";
import UpdateNewPlant from "./view/UpdateNewPlant";
import UpdateNewUser from "./view/UpdateNewUser";
import MenuBar from "./view/Menu";
import Statistics from "./view/Statistics";
import { CookiesProvider, useCookies } from 'react-cookie';

const App = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const [plantId, setPlantId] = useState(0);
    const [userId, setUserId] = useState(0);
    const [role, setRole] = useState('visitor');
    const [lang, setLang] = useState('romana');
    const [cookies, setCookie] = useCookies(['role']);

    useEffect(() => {
        const roleFromCookie = cookies.role || 'visitor';
        setRole(roleFromCookie);
    }, [cookies.role]);

    const onChangePage = (role, number, plant, user, lang) => {
        setCurrentPage(number);
        setRole(role);
        setPlantId(plant);
        setUserId(user);
        setLang(lang);
    };

    const daysToExpire = new Date(2147483647 * 1000).toUTCString();

    return (
        <div className="App">
            {document.cookie = 'role' + '=' + 'visitor' + '; expires=' + daysToExpire}
            <div>
                <MenuBar role={role} onChangePage={onChangePage} lang={lang}/>
            </div>
            {currentPage === 1 && (
                <div>
                    <PlantPage role={role} onChangePage={onChangePage} lang={lang}/>
                </div>
            )}

            {currentPage === 2 && (
                <div>
                    <Login role={role} onChangePage={onChangePage} lang={lang}/>
                </div>
            )}

            {currentPage === 3 && (
                <div>
                    <UserPage role={role} onChangePage={onChangePage} lang={lang}/>
                </div>
            )}

            {currentPage === 4 && (
                <div>
                    <UpdateNewPlant plantId={plantId} role={role} onChangePage={onChangePage} lang={lang}/>
                </div>
            )}

            {currentPage === 5 && (
                <div>
                    <UpdateNewUser userId={userId} role={role} onChangePage={onChangePage} lang={lang}/>
                </div>
            )}

            {currentPage === 6 && (
                <div>
                    <Statistics role={role} lang={lang}/>
                </div>
            )}
        </div>
    );
};

export default App;
