import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../style/UpdateNewPlant.css'

const UpdateNewPlant = ({ plantId, role, onChangePage}) => {
    const inputRef = useRef();
    const [plant, setPlant] = useState({
        plantName: '',
        species: '',
        plantType: '',
        carnivorous: '',
        endangered: '',
        location: '',
        img: '' // Assuming imageData is a base64-encoded string for simplicity
    });

    useEffect(() => {
        if (plantId) {
            // Fetch plant data for editing if plantId is provided
            axios.get(`http://localhost:8080/plant/findById?id=${plantId}`)
                .then(response => {
                    setPlant(response.data);
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }, [plantId]);

    const onButtonClickCancel = () => {
        onChangePage(role, 1, 0, 0);
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPlant(prevPlant => ({
            ...prevPlant,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (plantId) {
            // Update existing plant data
            axios.put(`http://localhost:8080/plant/update`, plant)
                .then(response => {
                    console.log('Plant updated successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error updating plant data:', error);
                });
        } else {
            // Add new plant data
            axios.post('http://localhost:8080/plant/add', plant)
                .then(response => {
                    console.log('New plant added successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error adding new plant:', error);
                });
        }

        onChangePage(role, 1, 0, 0);
    };

    const selectImage = () => {
        inputRef.current.click();
    };

    async function getBase64(file) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader()
            reader.readAsDataURL(file)
            reader.onload = () => {
                resolve(reader.result)
            }
            reader.onerror = reject
        })
    }

    const updatePhoto = async (e) => {
        const file = e.target.files[0];
        if (file) {
            try {
                //update or add image
                await getBase64(file) // `file` your img file
                    .then(res => {
                        setPlant(prev => ({...prev, img: res.split(',')[1]}));
                        axios.put(`http://localhost:8080/plant/photo?id=${plantId}&file=${res.split(',')[1]}`)
                            .then(response => {
                                console.log('New photo added successfully:', response.data);
                                // Optionally redirect to another page or display a success message
                            })
                            .catch(error => {
                                console.error('Error adding photo:', error);
                            });
                        console.log(res);
                    })
                    .catch(err => console.log(err))
            } catch (error) {
                console.log(error);
            }
        }
    };


    return (
        <div>
            <h2>{plantId ? 'Edit Plant' : 'Add New Plant'}</h2>
            <form onSubmit={handleSubmit}>
                <label>Plant name:</label>
                <input type="text" name="name" value={plant.plantName} onChange={handleChange} />
                <label>Species:</label>
                <input type="text" name="species" value={plant.species} onChange={handleChange} />
                <label>Plant type:</label>
                <input type="text" name="type" value={plant.plantType} onChange={handleChange} />
                <label>Carnivorous:</label>
                <input type="text" name="carnivorous" value={plant.carnivorous} onChange={handleChange} />
                <label>Endangered:</label>
                <input type="text" name="endangered" value={plant.endangered} onChange={handleChange} />
                <label>Location:</label>
                <input type="text" name="location" value={plant.location} onChange={handleChange} />
                <label>Image Data:</label>
                <div align={"center"}>
                    <img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px', alignContent: "center"}} />
                </div>
                <div align={"center"}>
                    <input type='file' onChange={updatePhoto} name='file' accept='image/*' />
                </div>
                <div>
                    <button onClick={onButtonClickCancel}>Cancel</button>
                    <button type="submit">{plantId ? 'Update Plant' : 'Add Plant'}</button>
                </div>
            </form>
        </div>
    );
};

export default UpdateNewPlant;
