import React, { Component } from 'react';
import axios from 'axios';
import '../style/PlantPage.css'; // Import CSS file
import Select from 'react-dropdown-select';

class PlantPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            plants: [],
            dict: [],
            selectedRow: null,
            textToSearch: '',
            lastLang: null
        };

        this.handleChange = this.handleChange.bind(this);
        this.presenter = new PlantPresenter();
    }

    componentDidMount() {
        const { lang } = this.props;
        // Fetch plant data from Java Spring backend
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });

        axios.get(`http://localhost:8080/lang/get?lang=${lang}`)
            .then(response => {
                this.setState({ dict: response.data, lastLang: lang});
                console.log(response.data);
            })
            .catch(error => {
                console.error('Error fetching language info:', error);
            });
    }

    handleRowClick = (plantId) => {
        this.setState({ selectedRow: plantId });
    };

    handleDelete = (id) => {
        axios.delete(`http://localhost:8080/plant/deleteById?id=${id}`)
            .then(response => {
                console.log(response.data);
                this.setState(prevState => ({
                    plants: prevState.plants.filter(plant => plant.id !== id)
                }));
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    };

    handleChange = (e) => {
        const { value } = e.target;
        this.setState({ textToSearch: value });
    };

    handleEdit = (plantId) => {
        const { role, onChangePage, lang } = this.props;
        onChangePage(role, 4, plantId, 0, lang);
    };

    handleSearch() {
        axios.get(`http://localhost:8080/plant/findByName?name=${this.state.textToSearch}`)
            .then(response => {
                console.log(response.data);
                axios.get('http://localhost:8080/plant/list')
                    .then(responseFull => {
                        this.setState({ plants: responseFull.data.filter(plant => plant.id === response.data.id) });
                    })
                    .catch(error => {
                        console.error('Error fetching plant data:', error);
                    });
            })
            .catch(error => {
                console.error('Error deleting data:', error);
            });
    }

    showAll() {
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                this.setState({ plants: response.data });
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }

    onChangeSort(values) {
        console.log(values);
        if (values === null || values.length === 0) {
            this.showAll();
        }
        else {
            axios.get(`http://localhost:8080/plant/sort?sortBy=${values[0].value}`)
                .then(response => {
                    this.setState({plants: response.data});
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }

    onChangeFilter(values) {
        console.log(values);
        if (values === null || values.length === 0) {
            this.showAll();
        }
        else {
            axios.get(`http://localhost:8080/plant/filter?criterion=${values[0].value}&value=${this.state.textToSearch}`)
                .then(response => {
                    this.setState({plants: response.data});
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });
        }
    }

    onChangeSave(values) {
        console.log(values);
        if (values === null || values.length === 0) {
            this.showAll();
        }
        else {
            axios.get(`http://localhost:8080/plant/save?format=${values[0].value}`)
                .then(response => {
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching plant data:', error);
                });

            window.open(`http://localhost:8080/plant/save?format=${values[0].value}`, '_blank');
        }
    }

    handleStats() {
        const { role, onChangePage, lang } = this.props;
        onChangePage(role, 6, 0, 0, lang);
    }

    render() {
        if (this.props.lang !== this.state.lastLang) {
            axios.get(`http://localhost:8080/lang/get?lang=${this.props.lang}`)
                .then(response => {
                    this.setState({dict: response.data, lastLang: this.props.lang });
                    console.log(response.data);
                })
                .catch(error => {
                    console.error('Error fetching language info:', error);
                });
        }

        const sortOptions = [
            {
                value: 'Tip',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Type"] : "Type"
            },
            {
                value: 'Specie',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Species"] : "Species"
            }
        ];

        const filterOptions = [
            {
                value: 'Tip',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Type"] : "Type"
            },
            {
                value: 'Specie',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Species"] : "Species"
            },
            {
                value: 'Carnivore',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Carnivorous"] : "Carnivorous"
            },
            {
                value: 'In pericol de disparitie',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Endangered"] : "Endangered"
            },
            {
                value: 'Locatie',
                label: this.state.dict["lang"] ? this.state.dict["lang"]["Location"] : "Location"
            }
        ];

        const saveOptions = [
            {
                value: 'csv',
                label: 'csv'
            },
            {
                value: 'json',
                label: 'json'
            },
            {
                value: 'xml',
                label: 'xml'
            },
            {
                value: 'docx',
                label: 'doc'
            }
        ];

        return (
            <div className="PlantPage">
                <h1>{this.state.dict["lang"] ? this.state.dict["lang"]["Plant page"] : "Plants"}</h1>
                <div>
                    <div className="combobox" style={{ width: 300, display: "flex" }}>
                        <label style={{margin: 9}}>{this.state.dict["lang"] ? this.state.dict["lang"]["Sort"] : "Sort by: "}: </label>
                        <Select
                            multi
                            options={sortOptions}
                            onChange={(values) => this.onChangeSort(values)}
                        />
                    </div>
                    <div className="combobox" style={{ width: 300, display: "flex"}}>
                        <label style={{margin: 6}}>{this.state.dict["lang"] ? this.state.dict["lang"]["Filter"] : "Filter by: "}: </label>
                        <Select
                            multi
                            options={filterOptions}
                            onChange={(values) => this.onChangeFilter(values)}
                        />
                    </div>
                    {this.props.role === 'employee' &&
                    <div className="combobox" style={{ width: 300, display: "flex"}}>
                        <label style={{margin: 6}}>{this.state.dict["lang"] ? this.state.dict["lang"]["Format"] : "Save as: "}: </label>
                        <Select
                            multi
                            options={saveOptions}
                            onChange={(values) => this.onChangeSave(values)}
                        />
                    </div>}
                </div>
                <div>
                    <input type="text" value={this.state.textToSearch} style={{ width: 500 }} onChange={this.handleChange}/>
                    <button className="statistics" onClick={() => this.handleSearch()}>{this.state.dict["lang"] ? this.state.dict["lang"]["Search"] : "Search"}</button>
                    <button className="statistics" onClick={() => this.showAll()}>{this.state.dict["lang"] ? this.state.dict["lang"]["Show"] : "Show all"}</button>
                </div>
                <table>
                    <thead>
                    <tr>
                        <th>No.</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Plant name"] : "Plant name"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Species"] : "Species"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Type"] : "Type"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Plant alim"] : "Carnivorous"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Endangered"] : "Endangered"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Location"] : "Location"}</th>
                        <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Image"] : "Image"}</th>
                        {this.props.role === 'employee' && <th>{this.state.dict["lang"] ? this.state.dict["lang"]["Actions"] : "Actions"}</th>}
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.plants.map(plant => (
                        <tr key={plant.id}
                            onClick={() => this.handleRowClick(plant.id)}
                            className={this.state.selectedRow === plant.id ? 'selected' : ''}>
                            <td>{plant.id}</td>
                            <td>{plant.plantName}</td>
                            <td>{plant.species}</td>
                            <td>{plant.plantType}</td>
                            <td>{plant.carnivorous}</td>
                            <td>{plant.endangered}</td>
                            <td>{plant.location}</td>
                            <td><img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px' }} /></td>
                            {this.props.role === 'employee' &&
                            <td>
                                <button onClick={() => this.handleDelete(plant.id)}>{this.state.dict["lang"] ? this.state.dict["lang"]["Delete"] : "Type"}</button>
                                <button onClick={() => this.handleEdit(plant.id)}>{this.state.dict["lang"] ? this.state.dict["lang"]["Edit"] : "Type"}</button>
                            </td>}
                        </tr>
                    ))}
                    </tbody>
                </table>

                {this.props.role === 'employee' && <button className="statistics" onClick={() => this.handleStats()}>{this.state.dict["lang"] ? this.state.dict["lang"]["Statistics"] : "View statistics"}</button>}
            </div>
        );
    }
}

export default PlantPage;
