import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../style/PlantPage.css'; // Import CSS file

const PlantPage = ({role, onChangePage}) => {
    const [plants, setPlants] = useState([]);
    const [selectedRow, setSelectedRow] = useState(null);

    useEffect(() => {
        // Fetch plant data from Java Spring backend
        axios.get('http://localhost:8080/plant/list')
            .then(response => {
                setPlants(response.data);
            })
            .catch(error => {
                console.error('Error fetching plant data:', error);
            });
    }, []);

    const handleRowClick = (plantId) => {
            setSelectedRow(plantId);
    };

    return (
        <div className="PlantPage">
            <h1>Plants</h1>
            <table>
                <thead>
                <tr>
                    <th>No.</th>
                    <th>Plant name</th>
                    <th>Species</th>
                    <th>Plant type</th>
                    <th>Carnivorous</th>
                    <th>Endangered</th>
                    <th>Location</th>
                    <th>Image</th>
                </tr>
                </thead>
                <tbody>
                {plants.map(plant => (
                    <tr key={plant.id}
                        onClick={() => handleRowClick(plant.id)}
                        className={selectedRow === plant.id ? 'selected' : ''}>
                        <td>{plant.id}</td>
                        <td>{plant.plantName}</td>
                        <td>{plant.species}</td>
                        <td>{plant.plantType}</td>
                        <td>{plant.carnivorous}</td>
                        <td>{plant.endangered}</td>
                        <td>{plant.location}</td>
                        <td><img src={`data:image/png;base64,${plant.img}`} alt="Plant" style={{ width: '200px', height: '200px' }}  /></td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default PlantPage;
