import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../style/UpdateNewUser.css'

const UpdateNewUser = ({ userId }) => {
    const [user, setUser] = useState({
        username: '',
        person: '',
        password: '',
        adminStatus: ''
    });
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        dateOfBirth: '',
        address: '',
        phoneNumber: '',
        email: ''
    });

    useEffect(() => {
        if (userId) {
            // Fetch user data for editing if plantId is provided
            axios.get(`http://localhost:8080/users/findById?id=${userId}`)
                .then(response => {
                    setUser(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user data:', error);
                });

            axios.get(`http://localhost:8080/employee/findByUser?id=${userId}`)
                .then(response => {
                    setEmployee(response.data);
                })
                .catch(error => {
                    console.error('Error fetching personal data:', error);
                });

        }
    }, [userId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser(prevUser => ({
            ...prevUser,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (userId) {
            // Update existing plant data
            axios.put(`http://localhost:8080/users/update/`, user)
                .then(response => {
                    console.log('User updated successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error updating user data:', error);
                });
        } else {
            // Add new plant data
            axios.post('http://localhost:8080/users/add', user)
                .then(response => {
                    console.log('New user added successfully:', response.data);
                    // Optionally redirect to another page or display a success message
                })
                .catch(error => {
                    console.error('Error adding new user:', error);
                });
        }
    };


    return (
        <div>
            <h2>{userId ? 'Edit User' : 'Add New User'}</h2>
            <form onSubmit={handleSubmit}>
                <label>First name:</label>
                <input type="text" name="firstName" value={employee.firstName} onChange={handleChange} />
                <label>Last name:</label>
                <input type="text" name="lastName" value={employee.lastName} onChange={handleChange} />
                <label>Date of birth:</label>
                <input type="text" name="type" value={employee.dateOfBirth} onChange={handleChange} />
                <label>Address:</label>
                <input type="text" name="carnivorous" value={employee.address} onChange={handleChange} />
                <label>Phone number:</label>
                <input type="text" name="endangered" value={employee.phoneNumber} onChange={handleChange} />
                <label>Email:</label>
                <input type="text" name="location" value={employee.email} onChange={handleChange} />
                <label>Username:</label>
                <input type="text" name="location" value={user.username} onChange={handleChange} />
                <label>Password:</label>
                <input type="text" name="location" value={user.password} onChange={handleChange} />
                <label>Admin status:</label>
                <input type="text" name="location" value={user.adminStatus} onChange={handleChange} />
                <button type="submit">{userId ? 'Update User' : 'Add User'}</button>
            </form>
        </div>
    );
};

export default UpdateNewUser;
